function rule2(d, str1) {
    let page = MY_PAGE - 1;
    let html1 = get1("http://search.kuwo.cn/r.s?stype=albuminfo&user=5b5fef483f589107&uid=2645060848&loginUid=0&loginSid=null&prod=kwplayer_ar_9.1.8.1&bkprod=kwbook_ar_9.1.8.1&source=kwplayer_ar_9.1.8.1_t87.apk&bksource=kwbook_ar_9.1.8.1_t87.apk&corp=kuwo&albumid="+str1+"&pn="+page+"&rn=100&show_copyright_off=1&vipver=MUSIC_8.2.0.0_BCS17&mobi=1&sortby=3&show_digitalmusic_off=1&iskwbook=1"); 
    if (page === 0) {
        d.push({
            title: html1.name,
            desc: html1.title+"\n集数："+html1.songnum,
            img: html1.img,
            url: "hiker://empty",
            col_type: "movie_1_vertical_pic_blur",
            extra: {
                gradient: true
            }
        });
        setDesc(d, html1.info);
    }
    html1.musiclist.forEach(it => {
        d.push({
            title: it.name,
            url: $("").lazyRule((musicrid) => {
                let {
                    get1
                } = $.require("hanshu");
                function formatToMinSecMs(secondsFloat) {
                  let minutes = Math.floor(secondsFloat / 60);
                  let seconds = Math.floor(secondsFloat % 60);
                  let milliseconds = Math.round((secondsFloat % 1) * 1000);
                  let pad2 = num => String(num).padStart(2, '0');
                  let pad3 = num => String(num).padStart(3, '0');
                  return `[${pad2(minutes)}:${pad2(seconds)}.${pad3(milliseconds)}]`;
                }
                let play1 = get1("http://mobi.kuwo.cn/mobi.s?f=web&user=0&source=kwplayercar_ar_6.0.0.9_B_jiakong_vh.apk&type=convert_url_with_sign&rid="+musicrid+"&br=128kmp3").data.url;
                let html1 = get1("https://www.kuwo.cn/openapi/v1/www/lyric/getlyric?musicId="+musicrid+"&httpsStatus=1&plat=web_www&from"); //log(html1)
                if (html1.data.lrclist) {
                    try {
                        let lrclist = [];
                        html1.data.lrclist.forEach(it => {
                            lrclist.push(`${formatToMinSecMs(it.time)} ${it.lineLyric}`);
                        });
                        return JSON.stringify({
                            urls: [play1 + "#isMusic=true#"],
                            lyric: lrclist.join("\n")
                        });
                    } catch(e) {
                        log(e.message);
                        if (/http/.test(play1)) return play1 + "#isMusic=true#";
                    }
                } else {
                    if (/http/.test(play1)) return play1 + "#isMusic=true#";
                }
            }, it.musicrid),
            col_type: "text_1",
            extra: {
                id: it.musicrid
            }
        });
    });
}

